import { toHaveStyle } from '@testing-library/jest-dom/dist/matchers';
import React from 'react'
import { Slide } from '../node_modules/react-slideshow-image/dist';

// takes an array of information
class SlideShow extends React.Component{
    constructor(props){
        super(props)
        project = [
            {name:"Cookflex", img: "/public/logo512.png", repository: "https://github.com/Dund0/FoodApp", date:"JANUARY 2020 - MAY 2021 ", description: "Created a mobile app for Android in Android Studio. I worked on the backend with Firebase, a service by Google for mobile and web applications, to manage users and their data, like images and emails. "},
            {name:"Speech-to-Text Bot", img: "/public/text-to-speech.png", repository: "https://github.com/CSUN-ACM/Speech-To-Text", date:"JANUARY 2020 - MAY 2021 ", description: "Utilizing Python and its various libraries, we created a bot for converting a person's speech to text. I helped in creating the model for the bot and then testing it with PyAudio "},
            {name:"Quiz App", img: "/public/quiz.jpg", repository: "https://github.com/br8832/COMP484", date:"JANUARY 2021 - MAY 2021", description: "Create a quiz game in meteor. I created the backend for interfacing with some API's and designed the look of our quiz game. Then, with the meteor tools, I hosted it for a short while"},
            {name:"This website", img: "/public/logo192.png", repository: "https://github.com/br8832/portfolio", date:"MARCH 2022 - APRIL2022", description: "Recreated a resume site from a class but using React. The purpose of which is to understand React better and update continuously my experience."}
        ]
        this.properties = {
            indicators: true,
            autoplay: true,
            canSwipe: true,
            arrows: true,
        }
    }
    render(){
        return (
            <section style={"color: orange"}>
            <Slide {...this.properties}>
                {this.projects.map( (project) => { return <><h2>{project.name}</h2>
                <a href={project.repository}><img src={project.img}></img></a>
                <h3>{project.date}</h3>
                <p>{project.desc}</p></>})}
            </Slide>
            </section>
        )
    }
}
export default SlideShow
